# // Connect callbacks // When a button is clicked, `number` and label of the other button will be changed button_increase.connect_clicked(clone!(@strong number, @strong button_decrease => move |_| { number.set(number.get() + 1); button_decrease.set_label(&number.get().to_string()); })); button_decrease.connect_clicked(clone!(@strong button_increase => move |_| { number.set(number.get() - 1); button_increase.set_label(&number.get().to_string()); }));
# # let gtk_box = gtk::Box::new(Orientation::Vertical, 0);
# window.set_child(Some(&gtk_box));
# gtk_box.append(&button_increase);
# gtk_box.append(&button_decrease);
