# // Trait shared by all GObjects
impl ObjectImpl for CustomButton { fn properties() -> &'static [ParamSpec] { static PROPERTIES: Lazy<Vec<ParamSpec>> = Lazy::new(|| { vec![ParamSpec::new_int( // Name "number", // Nickname "number", // Short description "number", // Minimum value i32::MIN, // Maximum value i32::MAX, // Default value 0, // The property can be read and written to ParamFlags::READWRITE, )] }); PROPERTIES.as_ref() } fn set_property(&self, _obj: &Self::Type, _id: usize, value: &Value, pspec: &ParamSpec) { match pspec.name() { "number" => { let input_number = value.get().unwrap(); self.number.replace(input_number); } _ => unimplemented!(), } } fn property(&self, _obj: &Self::Type, _id: usize, pspec: &ParamSpec) -> Value { match pspec.name() { "number" => self.number.get().to_value(), _ => unimplemented!(), } } fn constructed(&self, obj: &Self::Type) { self.parent_constructed(obj); // Bind label to number // `SYNC_CREATE` ensures that the label will be immediately set obj.bind_property("number", obj, "label") .flags(BindingFlags::SYNC_CREATE) .build(); }
}
# # // Trait shared by all widgets
